<?php

/**
 * Enqueue Google Web Fonts.
 */
 
function gosolar_default_fonts_url() {
    $font_url = '';
    
    /*
    Translators: If there are characters in your language that are not supported
    by chosen font(s), translate this to 'off'. Do not translate into your own language.
     */
    if ( 'off' !== _x( 'on', 'Google font: on or off', 'gosolar' ) ) {
        $font_url = add_query_arg( 'family', urlencode( 'Open Sans:300,400,600,700,800,300italic,400italic,600italic,700italic,800italic|Roboto Slab:100,300,400,700' ), "//fonts.googleapis.com/css" );
    }
    return $font_url;
}
 
function gosolar_enqueue_google_web_fonts() {
	if ( !class_exists( 'GoSolarthemesCore_Plugin' ) ) {
		wp_enqueue_style( 'google-fonts', gosolar_default_fonts_url(), array(), null, 'all' );
	}
}
add_action( 'wp_enqueue_scripts', 'gosolar_enqueue_google_web_fonts' );
function gosolar_default_theme_options(){
	$cur_theme = wp_get_theme(); 

	if ( $cur_theme->get( 'Name' ) == 'GoSolar' || $cur_theme->get( 'Name' ) == 'GoSolar Child' ){

		$zozo_opt = get_option('zozo_options');
		if( !$zozo_opt ){
			$input_val = gosolar_default_theme_values();
			$zozo_options = json_decode( $input_val, true );
			delete_option( 'zozo_options' ); 
			update_option( 'zozo_options', $zozo_options );
		}
		gosolar_save_theme_options();

	}

}

if ( ! class_exists( 'Redux' ) ) {
	add_action( 'wp_enqueue_scripts', 'gosolar_enqueue_google_web_fonts' );
	add_action("after_switch_theme", "gosolar_default_theme_options", 10);
}

